import axios from 'axios';
import * as actions from './types';

export const createUser = (newUser, history) => dispatch => {
  axios.post('/users/register')
    .then(res => {
      console.log(res);
      dispatch({
        type: actions.NEW_USER,
        payload: 'success'
      })
    })
    .catch(err => {
      console.log(err);

      dispatch({
        type: actions.NEW_USER,
        payload: 'errors'
      })
    })
  console.log(newUser);
  console.log(history);
};
