import React, { Component } from 'react';
import InputGroup from '../common/InputGroup';
import { verifyEmail, resetPassword} from '../actions/usersActions';
import { connect } from 'react-redux';

export class Forgot extends Component {
  state = {
    email: '',
    message: '',
    success: null
  }

  componentDidMount() {
    if (typeof this.props.errors === 'object' &&
        Object.keys(this.props.errors).length !== 0) {
      // has errors (reset password URL has expired)
      this.setState({
        message: this.props.errors.error_msg,
        success: false
      })
    }
  }

  onSubmit = (e) => {
    e.preventDefault();
    this.props.verifyEmail(this.state.email);
  }

  onChange = (e) => {
    this.setState({
      email: e.target.value
    })
  }

  componentWillReceiveProps(nextProps) {
    const { errors, success} = nextProps;

    if (Object.keys(errors).length !== 0){
      this.setState({
        success: false,
        message: errors.err_message
      })
    } else if (Object.keys(success).length !== 0) {
      this.setState({
        success: true,
        message: success.success_msg
      })
    } else {
      this.setState({
        success: null
      })
    }
  }

  render() {
    return (
      <div className="row mb-5 ml-auto mr-auto" id="forgotPage">
        <div className="col-md-6 m-auto">
          <img src="/img/logo.png" className="mx-auto d-block"
            id="appLogo" alt="logo" width="200" height="200" />

          <div className="card card-body shadow">
            <h1 className="text-center mb-3">
              <i className="fas fa-lock">&nbsp;</i> Reset your password
            </h1>
            {
              this.state.success !== null && this.state.success ? 
                <div className="alert alert-success alert-dismissible fade show"
                  role="alert">{this.state.message}
                </div> : 
                <div className="alert alert-danger alert-dismissible fade show"
                  role="alert">{this.state.message}
                </div>
            }
            <form onSubmit={this.onSubmit}>
              <InputGroup htmlFor="email"
                label="Please enter your email address"
                type="email"
                id="email"
                name="email"
                onChange={this.onChange}
                error={
                  (this.state.success === null || this.state.success) ? false : this.state.message
                }
                placeholder="Enter email address" />

              <p className="lead"><a href='/users/login'>Back to log in</a></p>
              <button type="submit" id="btn-sign-in" className="btn btn-primary btn-block">Send</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  errors: state.errors,
  success: state.success
})

export default connect(mapStateToProps, { verifyEmail, resetPassword})(Forgot);
