import React, { Component } from 'react';
import { connect } from 'react-redux';

class Dashboard extends Component {
  onChange = (e) => {

  }

  render() {
    return (
      <div>
        <div className="container mb-5">
          <h1 className="mt-4">Dashboard</h1>
          {
            this.props.auth.isAuthenticated ? 
            // authenticated
            <div className="col-md-6" >
                <img src={this.props.auth.user.imageUrl !== '' ? this.props.auth.user.imageUrl : "/img/logo.png"}
                  alt="user-icon" id='profileImg'
                  width="100" height="100" className="img-thumbnail rounded-circle" /> <br /><br />

                <p className="lead text-dark font-weight-bold"><i class="fas fa-user-edit"></i> Edit information:</p>
                <p className="lead text-dark">Edit profile image:</p>

                <form encType="multipart/form-data">
                  <div className="form-group row" style={{ 'display': 'flex', 'alignItems': 'center' }} >
                    <input className="col-7" type="file" accept="image/*" name="photo" />
                    <input className="col-5" type="submit" value="upload" className="btn btn-outline-primary btn-sm" />
                  </div>
                </form> <br />

                <form >
                  <div className="form-group row" style={{ 'display': 'flex', 'alignItems': 'center' }}>
                    <label htmlFor="editName" className="col-md-4 col-form-label text-dark">Edit name:</label>
                    <div className="col-md-8 mt-2">
                      <input type="editName" className="form-control" 
                            id="editName" placeholder="Enter new name"
                            onChange={this.onChange}
                            value={this.props.auth.user.name} />
                    </div>
                  </div>
                </form>

                <div className="form-group row" style={{ 'display': 'flex', 'alignItems': 'center' }}>
                  <fieldset disabled="">
                    <label htmlFor="email" className="col-md-4 col-form-label text-dark">Email:</label>
                    <div className="col-md-8 mt-2">
                      <input className="lead text-dark mb-2 form-control" value={this.props.auth.user.email} disabled="" />
                    </div>
                  </fieldset>
                </div>


            </div> :
            // not authenticated
            <div>
              <h2>Please sign in to view this!</h2>
              <a href="/users/login">Back to login</a>
            </div>
          }

        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps, null)(Dashboard);
