import React, { Component } from 'react';
import FacebookLogin from 'react-facebook-login';
import { facebookSignIn } from '../actions/usersActions';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';

class FacebookSignIn extends Component {
  state = {
    appId: '2540434142655804',
  }

  // Success sign in with Google
  onSuccess = (response) => {
    //console.log(response);
    //this.props.googleSignIn(response.tokenObj, this.props.history);
  }


  responseFacebook = (response) => {
    console.log(response);
  }

  render() {
    return (
      <FacebookLogin
        appId={this.state.appId}
        autoLoad={false}
        fields="name,email,picture"
        //onClick={componentClicked}
        render={renderProps => (
          <a href="#" id="facebook-button" className="btn btn-block btn-facebook" data-theme="dark">
            <i className="fas fa-facebook-f"></i> <span className='lead'>Sign in with Facebook</span>
          </a>
        )}
        callback={this.responseFacebook} />
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
})

export default connect(mapStateToProps, { facebookSignIn })(withRouter(FacebookSignIn));
