import React, { Component } from 'react';
import NavBar from '../layouts/NavBar';
import Footer from '../layouts/Footer';
import MyChart from '../common/charts/MyChart';
import AddAccount from './AddAccount';
import AccountDetails from './AccountDetails';
import data from '../fake-data/data';
import {loadAccounts} from '../actions/accountActions';
import {connect} from 'react-redux';

export class MainTools extends Component {
  state = {
    current: 'add-account',
    border_styles: 'border-top-0 border-bottom-0 border-left-0',
    account_content: null
  }

  componentDidMount() {
    // load all accounts
    this.props.loadAccounts();
  }

  onClick = (e) => {
    if (e.target.id !== 'manageAccount'){
      this.setState({
        current: e.target.id,
        account_content: 
        <div className="tab-pane fade" id={"content-" + e.target.id} role="tabpanel" aria-labelledby={"list-" + e.target.id + "-list"} >
          jhjhjh
        </div>
      })
    }
  }

  render() {
    const { current, border_styles, account_content} = this.state;
    const {accounts} = this.props;
    let account_list;

    console.log(account_list)
    if (accounts.loading) {
      account_list = <div className="d-flex p-2 justify-content-center">
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    } else {
      account_list = <div>
        {
          accounts.accounts.map((account, i) =>
            <a key={i} href={"#content-" + account.name} role="tab"
              onClick={this.onClick} id={account.name}
              className={"list-group-item list-group-item-action border-left" + border_styles + (current === account.name ? ' text-dark bg-light font-weight-bold' : '')} data-toggle="list">
              <span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>{account.name}
            </a>
          )
        }
      </div>
    }

    return (
      <div>
        <NavBar active="tools"/>
        <div className="container mt-3 mb-5">
          <div className="row justify-content-around">
            <div className="col-md-3 col-sm-12">
              <div className="list-group" role="tablist" >

                <a href="#account-list" id="manageAccount" onClick={this.onClick}
                  className="list-group-item list-group-item-action border-0"
                  role="tab" data-toggle="collapse" aria-expanded='true' onClick={this.onClick} >
                  <i className="fas fa-chevron-down">&nbsp;</i> Manage account
                </a>


                <div className="list-group collapse show" id="account-list">
                  {account_list}

                  <a href="#content-add-account" role="tab"
                    onClick={this.onClick} id="add-account"
                    className={"list-group-item list-group-item-action " + border_styles + (current === 'add-account' ? ' text-dark bg-light font-weight-bold' : '') } data-toggle="list">
                    <span>&nbsp;&nbsp;&nbsp;</span><i className="fas fa-plus-circle">&nbsp;&nbsp;</i>Add account
                  </a>
                </div>


                <a className={"list-group-item list-group-item-action " + border_styles + (current === 'statistics' ? ' text-dark bg-light font-weight-bold' : '') } 
                    onClick={this.onClick} id="statistics"
                    data-toggle="list" href="#content-statistics" role="tab" 
                    aria-controls="statistics">
                    <i className='fas fa-chart-bar'></i><span>&nbsp;&nbsp;</span>
                    What do I spend my money on?
                </a>
                <a className={"list-group-item list-group-item-action " + border_styles + (current === 'invest-calculator' ? ' text-dark bg-light font-weight-bold' : '') }
                  onClick={this.onClick} id="invest-calculator"
                  data-toggle="list" href="#content-invest-calculator" role="tab"
                  aria-controls="invest-calculator">
                  <i className='fas fa-cog'></i><span>&nbsp;&nbsp;</span>
                  Your investing calculator
                </a>

              </div>
            </div>


            <div className="col-md-9 mt-5">
              <div className="tab-content" id="nav-tabContent">
                {account_content}

                <div className="tab-pane fade show active" id="content-add-account" role="tabpanel" aria-labelledby="list-add-account-list">
                  <AddAccount/>
                </div>

                <div className="container tab-pane fade m-auto" id="content-statistics" role="tabpanel" aria-labelledby="list-statistics-list">
                  <MyChart type={'bar'} position={'bottom'} data={data.pie_bar_data} /><br/>
                  <MyChart type={'pie'} position={'right'} data={data.pie_bar_data} /> <br/>
                  <MyChart type={'line'} position={'bottom'} data={data.line_data} /> <br/>
                </div>

                <div className="tab-pane fade m-auto" id="content-invest-calculator" role="tabpanel" aria-labelledby="list-invest-calculator-list">
                  Investing calculator
                </div>
              </div>
            </div>
          </div>

        </div>

        <Footer />
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  accounts: state.accounts,
  success: state.success
})

export default connect(mapStateToProps, {loadAccounts})(MainTools);
