import React from 'react';
import { MDBDataTable, MDBInput } from 'mdbreact';

const DataTablePage = () => {
  const data = {
    columns: [
      {
        label: 'Select',
        field: 'check',
        width: 15
      },
      {
        label: 'Date',
        field: 'date',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Amount',
        field: 'amount',
        sort: 'desc',
        width: 120
      },
      {
        label: 'Category',
        field: 'category',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Balance',
        field: 'balance',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Notes',
        field: 'notes',
        width: 350
      },
      {
        label: 'Payee',
        field: 'payee',
        sort: 'asc',
        width: 200
      }
    ],
    rows: [
      {
        check: <MDBInput type="checkbox" />,
        date: 'April 14, 2019',
        amount: '$450',
        category: 'Bills',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        check: <MDBInput type="checkbox" />,
        date: 'Jan 15, 2019',
        amount: '$550',
        category: 'Groceries',
        balance: '$1,500',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        check: <MDBInput type="checkbox" />,
        date: 'Aug 16, 2019',
        amount: '$250',
        category: 'Cars',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        check: <MDBInput type="checkbox" />,
        date: 'Feb 14, 2019',
        amount: '$550',
        category: 'Tuition',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        check: <MDBInput type="checkbox" />,
        date: 'May 14, 2019',
        amount: '$150',
        category: 'Drinks',
        balance: '$500',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
    ]
  };

  return (
    <MDBDataTable
      scrollX
      data={data}
    />
  );
}

export default DataTablePage;
