import React, { Component } from 'react';
import DataTable from './DataTable';
import {loadTransactions} from '../actions/transactionActions';
import {connect} from 'react-redux';
import ModalDelete from '../common/ModalDelete';

class AccountDetails extends Component {

  componentDidMount() {
    const { name, id } = this.props;
    console.log(name + ', ' + id);
  }

  render() {
    const {name} = this.props;

    return (
      <div>
        <h2>
          <i className='fas fa-file-alt'>&nbsp;&nbsp;</i>Transactions history
        </h2><br/>
        <div className='d-flex'>
          <h4 className='mr-auto'>{name}</h4>
          <small className='mr-auto'>fdfs</small>
          <ModalDelete
            type='bank-account'
            header="Delete account"
            body="Delete this bank account will permanently delete all your information and cannot be undone. 
                Are you sure you want to do this?"
            buttonText="Delete"    
            cancelBtn="Close"
            yesBtn="Yes"
          />
        </div>
        <br/>

        <div className="table-hover table-responsive table-sm">
          <DataTable />
        </div>

      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  accounts: state.accounts,
  transactions: state.transactions,
  success: state.success
})

export default connect(mapStateToProps, {loadTransactions})(AccountDetails);
