import React, { Component } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { InputGroup} from './InputGroup';
import DatePicker from "react-datepicker";
import {connect} from 'react-redux';
import {addTransaction} from '../actions/transactionActions';

import "react-datepicker/dist/react-datepicker.css";

class ModalAdd extends Component {
  state = {
    show: false,
    date: new Date(),
    amount: '',
    category: 'General',
    notes: '',
    payee: this.props.auth.user.name,
  }


  handleChangeDate = (date) => {
    this.setState({
      date: date
    });
  }

  handleClose = () => {
    this.setState({ show: false });
  }

  handleShow = () => {
    this.setState({ show: true });
  }

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  onAdd = (e, bankId) => {
    e.preventDefault();
    var {date, amount, category, notes, payee} = this.state;

    this.props.addTransaction({
      date: date.toISOString().split('T')[0].replace(/-/g, '/'),
      amount: amount,
      category: category,
      notes: notes,
      payee: payee,
      bankId: bankId
    });
  }


  render() {
    const { styles, header, buttonText, cancelBtn, currency,
      mainFuncBtn, type, bankId, size, variant, disabled, icon, errors } = this.props;

    const {date, category, payee} = this.state;

    return (
      <div>
        <Button variant={variant} className={styles} onClick={this.handleShow} size={size} disabled={disabled} >
          <i className={icon}>&nbsp;&nbsp;</i>{buttonText}
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>
              <i className={icon}></i>
              <span>&nbsp;&nbsp;</span>{header}
            </Modal.Title>
          </Modal.Header>

          <Modal.Body>
            <label>Date 
              <span style={{ color: 'red' }}>&nbsp;*&nbsp;</span>
              <small className='text-muted'>(yyyy/mm/dd)</small>
            </label> <br/>
            <DatePicker
              dateFormat="yyyy/MM/dd"
              selected={date}
              onChange={this.handleChangeDate}
              className='mb-4'
            />
            <small className="form-text" style={{ color: 'red' }}>fsdfd</small>
            {errors.date && <small className="form-text" style={{ color: 'red' }}>{errors.date}</small>}

            <InputGroup
              htmlFor="amount"
              label={`Amount (${currency})`}
              type="amount"
              id="amount"
              name="amount"
              required={true}
              onChange={this.onChange}
              error={errors.amount}
              placeholder="Enter amount"
            />
            <InputGroup
              htmlFor="category"
              label="Category"
              type="category"
              id="category"
              name="category"
              onChange={this.onChange}
              value={category}
              placeholder="Enter category"
            />
            <InputGroup
              htmlFor="notes"
              label="Notes"
              type="notes"
              id="notes"
              name="notes"
              onChange={this.onChange}
              placeholder="Enter notes"
            />
            <InputGroup
              htmlFor="payee"
              label="Payee"
              type="payee"
              id="payee"
              name="payee"
              onChange={this.onChange}
              value={payee}
              placeholder="Enter payee"
            />
          </Modal.Body>

          <Modal.Footer>
            <Form onSubmit={(e) => this.onAdd(e, bankId)}>
              <Button variant="primary" className="mr-2" type="submit" onClick={this.handleClose}>
                {mainFuncBtn}
              </Button>
              <Button variant="secondary" type="button" onClick={this.handleClose}>
                {cancelBtn}
              </Button>
            </Form>
          </Modal.Footer>
        </Modal>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  errors: state.errors
})

export default connect(mapStateToProps, { addTransaction})(ModalAdd);
