import React, { Component } from 'react'
import Chart from "chart.js";
import {connect} from 'react-redux';
import { analysis} from '../../actions/dataAnalysis';
import randomColor from '../randomColors';
import * as actions from '../../actions/types';

class MyChart extends Component {
  state = {
    window_width: window.innerWidth
  }
  chartRef = React.createRef();

  componentDidMount() {
  }

  componentWillReceiveProps(nextProps) {
    const { accounts, transactions, success, dataAnalysis} = nextProps;

    if (!accounts.loading && !transactions.loading
      && Object.keys(accounts.account).length > 0 && Object.keys(transactions.account).length > 0
      && typeof success !== 'undefined' && success !== null && (success.type === actions.ADD_TRANSACTION || success.type === actions.UPDATE_TRANSACTION || success.type === actions.DELETE_TRANSACTION) ) {

      this.props.analysis(accounts, transactions);
      console.log(dataAnalysis)
      this.buildChart();
    }
  }

  buildChart = () => {
    const {window_width} = this.state;
    const { type, position, dataAnalysis} = this.props;

    window.addEventListener('resize', () => {
      this.setState({ window_width: window.innerWidth  })
    });

    const myChartRef = this.chartRef.current.getContext("2d");

    new Chart(myChartRef, {
      type: type,
      data: {
        labels: !dataAnalysis.analyzing ? dataAnalysis.categories : [],
        datasets: [
          {
            data: !dataAnalysis.analyzing ? dataAnalysis.data : [],
            backgroundColor: randomColor(!dataAnalysis.analyzing ? dataAnalysis.categories.length : 0)
          }
        ]
      },
      options: {
        title: {
          text: 'Chart',
          display: true
        },
        //responsive: true,
        legend: {
          display: true,
          position: window_width > 945 ? (type === 'pie' || type ==='doughnut' ? 'right' : position) : 'bottom',
          labels: {
            boxWidth: window_width < 600 ? 15 : 40,
            generateLabels: function(chart) {
              var data = chart.data;
              if (data.labels.length && data.datasets.length) {
                if (type !== 'line') {
                  return data.labels.map(function (label, i) {
                    return {
                      text: label + ': ' + data.datasets[0].data[i],
                      fillStyle: data.datasets[0].backgroundColor[i],
                      index: i
                    };
                  });
                } else {
                  return data.datasets.map(function(kind, i) {
                    return {
                      text: kind.label,
                      fillStyle: kind.borderColor
                    }
                  })
                }
              }
              return [];
            }
          }
        }
      }
    });
  }
  render() {
    const {window_width} = this.state;
    const { type, dataAnalysis } = this.props;

    return (
      <div>
        {
          dataAnalysis.analyzing ? 
            <div className="d-flex justify-content-center mb-4">
              <div className="spinner-border" role="status">
                <span className="sr-only">Loading...</span>
              </div>
            </div>
          : 
            <canvas
              id="myChart"
              style={{ height: window_width < 350 ? (type === 'pie' ? '300vh' : '350vh') : (type === 'horizontalBar' ? '350vh' : '250vh'), width: '400vw' }}
              ref={this.chartRef}
            />

        }

      </div>
    )
  }
}

const mapStateToProps = state => ({
  errors: state.errors,
  accounts: state.accounts,
  transactions: state.transactions,
  dataAnalysis: state.dataAnalysis
}) 

export default connect(mapStateToProps, {analysis})(MyChart);
