import React, { Component } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';

export class TransactionsTable extends Component {
  render() {
    const data = [
      {
        date: '2019/05/19',
        amount: '$45.89',
        category: 'bills',
        currency: 'USD',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/20',
        amount: '$45.89',
        category: 'bills',
        currency: 'USD',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/20',
        amount: '$45.89',
        category: 'bills',
        currency: 'USD',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/20',
        amount: '$45.89',
        category: 'bills',
        currency: 'USD',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/20',
        amount: '$45.89',
        category: 'bills',
        currency: 'USD',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
      {
        date: '2019/05/20',
        amount: '$45.89',
        category: 'bills',
        currency: 'USD',
        notes: 'Short notes for description',
        payee: 'Trung Vo'
      },
    ]

    const columns = [{
      Header: 'Date',
      accessor: 'date',
      id: 'date'
    }, {
      Header: 'Amount',
      accessor: 'amount',
    }, {
      Header: 'Category',
      accessor: 'category'
    }, {
      Header: 'Currency',
      accessor: 'currency'
    }, {
      Header: 'Notes',
      accessor: 'notes'
    }, {
      Header: 'Payee',
      accessor: 'payee'
    }]

    return (
      <ReactTable
        sorted={{ 
          id: 'date',
          desc: true
        }}
        data={data}
        columns={columns}
        defaultPageSize={10}
        pageSizeOptions={[5, 10, 15, 20]}
        defaultSorted={['date']}
      />
    )
  }
}

export default TransactionsTable;
