import { GET_SUCCESS, CLEAR_SUCCESS, 
  GET_ADD_SUCCESS, GET_DELETE_SUCCESS, 
  GET_UPDATE_SUCCESS } from '../actions/types';

const initialState = {
  add: '',
  delete: '',
  update: '',
  success_msg: ''
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_SUCCESS:
      return action.payload
    case GET_ADD_SUCCESS:
      return action.payload
    case GET_DELETE_SUCCESS:
      return action.payload
    case GET_UPDATE_SUCCESS:
      return action.payload
    case CLEAR_SUCCESS:
      return {};
    default:
      return state;
  }
}
