import React, { Component } from 'react';
import NavBar from '../layouts/NavBar';
import Footer from '../layouts/Footer';

export class MainTools extends Component {
  state = {
    current: ''
  }

  onClick = (e) => {
    this.setState({
      current: e.target.id
    })
  }

  render() {
    const {current} = this.state;
    return (
      <div>
        <NavBar/>
        <div className="container mt-2 mb-5">
          <div className="row justify-content-around">
            <div className="col-md-4 col-sm-12">
              <div className="list-group">

                <a href="#item-1" id="manageAccount" onClick={this.onClick}
                  className="list-group-item list-group-item-action border-0"
                  role="tab" data-toggle="collapse" style={{color: 'black', backgroundColor: 'white'}}>
                  <i className="fas fa-chevron-down">&nbsp;</i> Manage account
                </a>


                <div className="list-group collapse" id="item-1">
                  <a href="#content-account-1" role="tab" style={{ color: 'black', backgroundColor: 'white' }}
                    onClick={this.onClick} id="account-1"
                    className={"list-group-item list-group-item-action" + (current === 'account-1' ? ' border-dark border-left border-top-0 border-bottom-0 border-right-0' : ' border-0') } data-toggle="list">
                    <span>&nbsp;&nbsp;</span>Account 1
                  </a>
                  <a href="#content-account-2" role="tab" style={{ color: 'black', backgroundColor: 'white'}}
                    onClick={this.onClick} id="account-2"
                    className={"list-group-item list-group-item-action" + (current === 'account-2' ? ' border-dark border-left border-top-0 border-bottom-0 border-right-0' : ' border-0') } data-toggle="list">
                    <span>&nbsp;&nbsp;</span>Account 2
                  </a>
                  <a href="#content-account-3" role="tab" style={{ color: 'black', backgroundColor: 'white' }}
                    onClick={this.onClick} id="account-3"
                    className={"list-group-item list-group-item-action" + (current === 'account-3' ? ' border-dark border-left border-top-0 border-bottom-0 border-right-0' : ' border-0') } data-toggle="list">
                    <span>&nbsp;&nbsp;</span>Account 3
                  </a>
                </div>


                <a className={"list-group-item list-group-item-action" + (current === 'profile' ? ' border-dark border-left border-top-0 border-bottom-0 border-right-0' : ' border-0') } 
                    style={{ color: 'black', backgroundColor: 'white' }}
                    onClick={this.onClick} id="profile"
                    data-toggle="list" href="#content-profile" role="tab" 
                    aria-controls="profile">
                    Profile
                </a>
                <a className={"list-group-item list-group-item-action" + (current === 'messages' ? ' border-dark border-left border-top-0 border-bottom-0 border-right-0' : ' border-0') }
                  style={{ color: 'black', backgroundColor: 'white' }}
                  onClick={this.onClick} id="messages"
                  data-toggle="list" href="#content-messages" role="tab"
                  aria-controls="messages">
                  Messages
                </a>

              </div>
            </div>


            <div className="col-md-8">
              <div className="tab-content" id="nav-tabContent">
                <div className="tab-pane fade" id="content-account-1" role="tabpanel" aria-labelledby="list-home-list">Account 1</div>
                <div className="tab-pane fade" id="content-account-2" role="tabpanel" aria-labelledby="list-home-list">Account 2</div>
                <div className="tab-pane fade" id="content-account-3" role="tabpanel" aria-labelledby="list-home-list">Account 3</div>

                <div className="tab-pane fade" id="content-profile" role="tabpanel" aria-labelledby="list-profile-list">Profile</div>
                <div className="tab-pane fade" id="content-messages" role="tabpanel" aria-labelledby="list-messages-list">Message</div>
              </div>
            </div>
          </div>

        </div>
        <Footer/>
      </div>
    )
  }
}

export default MainTools;
