import React, { Component } from 'react';
import { connect } from 'react-redux';
import { deleteAccount } from '../actions/usersActions';
import {withRouter} from 'react-router-dom';
import {Modal, Button, Form} from 'react-bootstrap';

export class ModalDelete extends Component {
  state = {
    show: false
  }
  handleClose = () => {
    this.setState({ show: false });
  }

  handleShow = () => {
    this.setState({ show: true });
  }

  onDelete = (e) => {
    e.preventDefault();
    this.props.deleteAccount();
  }

  render() {
    const {header, body, buttonText, cancelBtn, yesBtn} = this.props;
    return (
      <div>
        {/* <button data-toggle="modal"
                data-target="#exampleModal"
                type="button" className="btn btn-danger">{buttonText}
        </button>

        <div className="modal fade" id="exampleModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">{header}</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                {body}
              </div>
              <form className="modal-footer" onSubmit={this.onDelete} >
                <button type="submit" data-dismiss=""
                   className="btn btn-outline-secondary">{yesBtn}
                </button>
                <button type="button" className="btn btn-primary" data-dismiss="modal">{cancelBtn}</button>
              </form>
            </div>
          </div>
        </div> */}
        <Button variant="danger" onClick={this.handleShow}>
          {buttonText}
        </Button>

        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>{header}</Modal.Title>
          </Modal.Header>
          <Modal.Body>{body}</Modal.Body>
          <Modal.Footer>
            <Form onSubmit={this.onDelete}>
              <Button variant="secondary" type="submit" onClick={this.handleClose}>
                {yesBtn}
              </Button>
              <Button variant="primary" type="button" onClick={this.handleClose}>
                {cancelBtn}
              </Button>
            </Form>
          </Modal.Footer>
        </Modal>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  success: state.success
});

export default connect(mapStateToProps, { deleteAccount })(withRouter(ModalDelete));
