import React, { Component } from 'react';
import {GoogleLogin} from 'react-google-login';

class GoogleSignIn extends Component {
  state = {
    clientId: '654747296638-vd111rdj4raet23ab1br75b84cgk4d2f.apps.googleusercontent.com',
    clientSecret: '9O8lu6TvrE2kiQxkfUDwvGET',

  }
  onSuccess = () => {

  }
  onFailure = () => {

  }
  render() {
    return (
      <GoogleLogin
        clientId="658977310896-knrl3gka66fldh83dao2rhgbblmd4un9.apps.googleusercontent.com"
        buttonText="Sign in with Google"
        render={renderProps => (
          <a href="/users/auth/google" id="google-button" className="btn btn-block btn-google" data-onsuccess="onSignIn" data-theme="dark">
            <i className="fa fa-google-plus fa-fw"></i> Sign in with Google
          </a>
        )}
        onSuccess={this.onSuccess}
        onFailure={this.onFailure}
        cookiePolicy={'single_host_origin'}
      />
    )
  }
}

export default GoogleSignIn;
