import * as React from 'react';
import currencyTypes from '../currencyTypes/currencyTypes';
import {
  PagingState,
  IntegratedPaging,
  DataTypeProvider,
  SearchState,
  IntegratedFiltering,
  EditingState
} from '@devexpress/dx-react-grid';

import {
  Grid, Table,
  TableHeaderRow, PagingPanel,
  SearchPanel, Toolbar,
  TableEditRow,
  TableEditColumn
} from '@devexpress/dx-react-grid-bootstrap4';

//import "@devexpress/dx-react-grid-bootstrap4/dist/dx-react-grid-bootstrap4.css";

const PaymentFormatter = (props) => {
  const {value, row} = props;
  const {currency} = row;
  return (
    <b style={{ color: value >= 0 ? 'green' : 'red' }}>
      {value >= 0 ? '+' : '-'}<small>{currency === 'USD' ? currencyTypes.usd : (currency === 'VND' ? currencyTypes.vnd : currencyTypes.eur)}</small>{Math.abs(value)}
    </b>
  );
}

const PaymentTypeProvider = props => {
  return (
    <DataTypeProvider
      formatterComponent={PaymentFormatter}
      {...props}
    />
  );
}

const BalanceFormatter = (props) => {
  const { value, row } = props;
  const { currency } = row;
  return (
    <b style={{ color: value >= 0 ? 'green' : 'red' }}>
      {value >= 0 ? '' : '-'}<small>{currency === 'USD' ? currencyTypes.usd : (currency === 'VND' ? currencyTypes.vnd : currencyTypes.eur)}</small>{Math.abs(value)}
    </b>
  );
}

const BalanceTypeProvider = props => (
  <DataTypeProvider
    formatterComponent={BalanceFormatter}
    {...props}
  />
);

const TableComponent = ({ ...restProps }) => (
  <Table.Table
    {...restProps}
    className="table-striped"
  />
);

const getRowId = row => row.id;

class DataTable extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      columns: [
        { name: 'date', title: 'Date' },
        { name: 'amount', title: 'Amount' },
        { name: 'category', title: 'Category' },
        { name: 'balance', title: 'Balance' },
        { name: 'currency', title: 'Currency' },
        { name: 'notes', title: 'Notes' },
        { name: 'payee', title: 'Payee' }
      ],
      tableColumnExtensions: [
        { columnName: 'date', align: 'center', width: 90 },
        { columnName: 'amount', align: 'right', width: 110 },
        { columnName: 'balance', align: 'right', width: 110 },
        { columnName: 'currency', align: 'right', width: 70 },
        { columnName: 'category', align: 'center', width: 130 },
        { columnName: 'notes', align: 'center', wordWrapEnabled: true, width: 350 },
        { columnName: 'payee', align: 'center', width: 180 }
      ],
      rows: [
        {
          id: 1,
          date: 'April 14, 2019',
          amount: 450,
          category: 'Bills',
          balance: 500,
          currency: 'VND',
          notes: '',
          payee: 'Trung Vo'
        },
        {
          id: 2,
          date: 'Jan 15, 2019',
          amount: -550,
          category: 'Groceries',
          balance: 1500,
          currency: 'EUR',
          notes: '',
          payee: 'Trung Vo'
        },
        {
          id: 3,
          date: 'Aug 16, 2019',
          amount: 250,
          category: 'Cars',
          balance: 500,
          currency: 'USD',
          notes: 'Short notes',
          payee: 'Trung Vo'
        },
        {
          id: 4,
          date: 'Feb 14, 2019',
          amount: -550,
          category: 'Tuition',
          balance: 500,
          currency: 'USD',
          notes: 'Short notes',
          payee: 'Trung Vo'
        },
        {
          id: 5,
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          currency: 'USD',
          notes: '',
          payee: 'Trung Vo'
        },
        {
          id: 6,
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          currency: 'USD',
          notes: '',
          payee: 'Trung Vo'
        },
        {
          id: 7,
          date: 'May 14, 2019',
          amount: -150,
          category: 'Drinks',
          balance: 500,
          currency: 'USD',
          notes: '',
          payee: 'Trung Vo'
        },
        {
          id: 8,
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          currency: 'USD',
          notes: '',
          payee: 'Trung Vo'
        },
        {
          id: 9,
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          currency: 'USD',
          notes: '',
          payee: 'Trung Vo'
        },
        {
          id: 10,
          date: 'May 14, 2019',
          amount: 150,
          category: 'Drinks',
          balance: 500,
          currency: 'USD',
          notes: '',
          payee: 'Trung Vo'
        }
      ],
      paymentColumns: ['amount'],
      balanceColumns: ['balance'],
      currentPage: 0,
      pageSize: 10,
      pageSizes: [10, 20, 30],
      searchValue: ''
    };

    this.changeCurrentPage = currentPage => this.setState({ currentPage });
    this.changePageSize = pageSize => this.setState({ pageSize });
    this.changeSearchValue = value => this.setState({ searchValue: value });

  }

  commitChanges = ({ added, changed, deleted }) => {
    console.log(added);

    let { rows } = this.state;
    if (added) {
      const startingAddedId = rows.length > 0 ? rows[rows.length - 1].id + 1 : 0;
      rows = [
        ...rows,
        ...added.map((row, index) => ({
          id: startingAddedId + index,
          ...row,
        })),
      ];
    }
    if (changed) {
      rows = rows.map(row => (changed[row.id] ? { ...row, ...changed[row.id] } : row));
    }
    if (deleted) {
      const deletedSet = new Set(deleted);
      rows = rows.filter(row => !deletedSet.has(row.id));
    }
    this.setState({ rows });
  }

  render() {
    const {
      rows, columns, tableColumnExtensions, searchValue,
      paymentColumns, balanceColumns, pageSize, pageSizes, currentPage,
    } = this.state;

    return (
      <Grid rows={rows} columns={columns} getRowId={getRowId} >
          <PagingState
            defaultCurrentPage={currentPage}
            onCurrentPageChange={this.changeCurrentPage}
            pageSize={pageSize}
            onPageSizeChange={this.changePageSize}
          />
          <PaymentTypeProvider
            for={paymentColumns}
          />
          <BalanceTypeProvider
            for={balanceColumns}
          />
          <SearchState
            value={searchValue}
            onValueChange={this.changeSearchValue}
          />
          <IntegratedFiltering />
          <Toolbar />
          <SearchPanel />

          <EditingState
            onCommitChanges={this.commitChanges}
          />

          <TableEditRow />
          <TableEditColumn
            showAddCommand
            showEditCommand
            showDeleteCommand
          />

          <IntegratedPaging />
          <Table columnExtensions={tableColumnExtensions} tableComponent={TableComponent} />
          <TableHeaderRow />
          <PagingPanel pageSizes={pageSizes} />
        </Grid>
    );

  }
}

export default DataTable;
