import * as React from 'react';
import {
  SortingState, SelectionState, FilteringState, GroupingState, SearchState,
  IntegratedFiltering, IntegratedGrouping, IntegratedSorting, IntegratedSelection,
} from '@devexpress/dx-react-grid';
import {
  Grid,
  VirtualTable, TableHeaderRow, TableFilterRow, TableSelection, TableGroupRow,
  GroupingPanel, DragDropProvider, TableColumnReordering, Toolbar, SearchPanel,
} from '@devexpress/dx-react-grid-bootstrap4';


const Cell = (props) => {
  const { column } = props;
  return <VirtualTable.Cell {...props} />;
};


class DataTable extends React.PureComponent {
  constructor(props) {
    super(props);

    this.state = {
      columns: [
        { name: 'date', title: 'Date' },
        { name: 'amount', title: 'Amount' },
        { name: 'category', title: 'Category' },
        { name: 'balance', title: 'Balance' },
        { name: 'notes', title: 'Notes' },
        { name: 'payee', title: 'Payee' }
      ],
      tableColumnExtensions: [
        { columnName: 'amount', align: 'right' },
        { columnName: 'balance', align: 'right' },
      ],
      rows: [
        {
          date: 'April 14, 2019',
          amount: '$450',
          category: 'Bills',
          balance: '$500',
          notes: 'Short notes for descriptionShort notes for descriptionShort notes for descriptionShort notes for description',
          payee: 'Trung Vo'
        },
        {
          date: 'Jan 15, 2019',
          amount: '$550',
          category: 'Groceries',
          balance: '$1,500',
          notes: 'Short notes for description',
          payee: 'Trung Vo'
        },
        {
          date: 'Aug 16, 2019',
          amount: '$250',
          category: 'Cars',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo'
        },
        {
          date: 'Feb 14, 2019',
          amount: '$550',
          category: 'Tuition',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo'
        },
        {
          date: 'May 14, 2019',
          amount: '$150',
          category: 'Drinks',
          balance: '$500',
          notes: 'Short notes for description',
          payee: 'Trung Vo'
        }
      ],
      currencyColumns: ['amount'],
    };
  }

  render() {
    const {
      rows, columns, tableColumnExtensions,
      currencyColumns
    } = this.state;

    return (
      <div className="card">
        <Grid
          rows={rows}
          columns={columns}
        >
          <DragDropProvider />

          <FilteringState
            defaultFilters={[{ columnName: 'saleDate', value: '2016-02' }]}
          />
          <SearchState />
          <SortingState
            defaultSorting={[
              { columnName: 'amount', direction: 'asc' },
              { columnName: 'date', direction: 'asc' },
            ]}
          />

          <SelectionState />

          <IntegratedFiltering />
          <IntegratedSorting />
          <IntegratedGrouping />
          <IntegratedSelection />


          <VirtualTable
            columnExtensions={tableColumnExtensions}
            cellComponent={Cell}
          />
          <TableHeaderRow showSortingControls />
          <TableColumnReordering defaultOrder={columns.map(column => column.name)} />
          <TableFilterRow showFilterSelector />
          <TableSelection showSelectAll />
          <TableGroupRow />
          <Toolbar />
          <SearchPanel />
          <GroupingPanel showSortingControls />
        </Grid>
      </div>
    );
  }
}

export default DataTable;
