import React, { Component } from 'react';
import { connect } from 'react-redux';

export class Main extends Component {
  state = {
    name: '',
    email: '',
    imageUrl: ''
  }

  componentDidMount() {
    if (this.props.auth.isAuthenticated) {
      this.setState({
        name: this.props.auth.user.name,
        email: this.props.auth.user.email,
        imageUrl: this.props.auth.user.imageUrl,
      })
    }
  }

  render() {
    return (
      <div className="row pt-5 pb-5 m-auto justify-content-around" id="mainPage">
        <div className="col-md-5">
          <div className="card card-body text-center shadow-lg">
            {
              !this.props.auth.isAuthenticated ? 
                <div>
                  <img src="/img/logo.png" className="m-auto"
                    id="appLogo" alt="logo" width="200" height="200" />

                  <p className="font-weight-bold">Create an account or login</p>
                  <a href="/users/register" className="btn btn-primary btn-block mb-2">Register</a>
                  <a href="/users/login" className="btn btn-outline-secondary btn-block">Login</a>
                  <br /> <p className="font-weight-bold">Or</p>

                  <a href="/users/auth/google" id="google-button" className="btn btn-block btn-google" data-onsuccess="onSignIn" data-theme="dark">
                    <i className="fa fa-google-plus fa-fw"></i> Sign in with Google
                  </a>
                  <a href="#" id="facebook-button" className="btn btn-block btn-facebook">
                    <i className="fa fa-facebook fa-fw"></i> Sign in with Facebook
                  </a> 
                </div> : 
                <div>
                  <img src={this.state.imageUrl} className="m-auto"
                    id="appLogo" alt="logo" width="200" height="200" />

                  <p className="font-weight-bold">{this.state.name}</p>
                  <p className="font-weight-bold">Email: {this.state.email}</p>
                </div>
            }

          </div>
        </div>
        <div className="col-md-5 text-center" id="welcomeSlogan" style={{ 'display': 'flex', 'alignItems': 'center' }}>
          <div>
            <h1 id="mainAppHeader">Smart Money</h1>
            <blockquote className="text-dark text-center" id="mainAppText" >
              <p>Keep track of your spending, create budget and stay out of debt. We provide all services and solutions you will ever need to achieve your financial goals.</p>
              <footer className="text-dark blockquote-footer">
                <img src='/img/logo.png' width='50' height='50' className='mr-2' />
                Trung Vo, <cite title="Source Title">Software Developer</cite>
              </footer>
            </blockquote>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  auth: state.auth
})

export default connect(mapStateToProps, null)(Main);
